// src/pages/Fashions.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import 'C:/Users/surid/Desktop/Web Stories/my-app/src/pages/Fashoin/Fashion.css'; // Ensure you have this CSS file for styling
import Header from '../../components/Header/Header';

function Fashion() {
  const [stories, setStories] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:5000/api/stories')
      .then((res) => res.json())
      .then((data) => {
        const fashionStories = data.filter(
          (story) => story.category?.toLowerCase() === 'fashion'
        );
        setStories(fashionStories);
      })
      .catch((err) => console.error('Error fetching stories:', err));
  }, []);

  return (
    <div className='container'>
      <Header/>
          <h2 style={{ textAlign: 'center' }} className='m-4'>Latest Fashion Stories</h2>
    <div className="fashions-container">
      <div className="story-grid">
          {stories.length === 0 ? (
        <p>No Fashion stories available.</p>
      ) : (
        stories.map((story) => (
          <div
            key={story._id}
            className="story-card"
            onClick={() => navigate(`/story/${story._id}`)}
          >
            <img src={story.thumbnail} alt={story.title} className="story-img" />
            {/* <h4>{story.title}</h4> */}
          </div>))
        )}
      </div>
    </div>
    </div>
  );
}

export default Fashion;
