// src/pages/TechInfo/TechInfo.js

import React, { useEffect, useState } from 'react';
import './TechInfo.css'; // optional styling
import { useNavigate } from 'react-router-dom';
import Header from '../../components/Header/Header';

function TechInfo() {
  const [stories, setStories] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:5000/api/stories')
      .then((res) => res.json())
      .then((data) => {
        const filtered = data.filter((item) => item.category === 'techinfo');
        setStories(filtered);
      })
      .catch((err) => console.error('Error fetching stories:', err));
  }, []);

  return (
    <div>
      <Header />
      <h2 style={{ textAlign: 'center' }} className='m-4'>Latest TechInfo Stories</h2>
    <div className="tech-grid">
      {stories.length === 0 ? (
        <p>No TechInfo stories available.</p>
      ) : (
        stories.map((story) => (
          <div
            className="story-card"
            key={story._id}
            onClick={() => navigate(`/story/${story._id}`)}
          >
            <img src={story.thumbnail} alt={story.title} className="story-img" />
            <h4>{story.title}</h4>
          </div>
        ))
      )}
    </div>
    </div>
  );
}

export default TechInfo;
