import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './Stories.css';

function Stories() {
  const { id } = useParams(); // ðŸ‘ˆ Get story ID from the URL
  const [story, setStory] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`http://localhost:5000/api/stories/${id}`)
      .then((res) => {
        if (!res.ok) throw new Error('Story not found');
        return res.json();
      })
      .then((data) => {
        setStory(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching story:', err.message);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <p className="loading-text">Loading...</p>;
  if (!story) return <p className="error-text">Story not found</p>;

  return (
    <div className="story-viewer">
      <div className="story-box">
        <img
          src={story.image}
          alt={story.title}
          className="story-full-img"
        />
        <div className="story-details">
          <h2>{story.title}</h2>
          <p>{story.quote}</p>
        </div>
      </div>
    </div>
  );
}

export default Stories;
