// src/pages/Electronics.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './StoryGrid.css'; // You can reuse the same styles

function Electronics() {
  const [stories, setStories] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:5000/api/stories')
      .then(res => res.json())
      .then(data => {
        const filtered = data.filter(story => story.category?.toLowerCase() === 'electronic');
        setStories(filtered);
      })
      .catch(err => console.error('Fetch error:', err));
  }, []);

  return (
    <div className="story-grid">
      {stories.length === 0 ? (
        <p>No electronics stories available.</p>
      ) : (
        stories.map(story => (
          <div
            key={story._id}
            className="story-card"
            onClick={() => navigate(`/story/${story._id}`)}
          >
            <img src={story.thumbnail} alt={story.title} className="story-img" />
            <div className="story-info">
              <h4>{story.title}</h4>
              <p>{story.category}</p>
            </div>
          </div>
        ))
      )}
    </div>
  );
}

export default Electronics;
