import React, { useState } from 'react';
import './QuoteStories.css'; // reuse same styles

const allStories = {
  quotes: [
    {
      image: 'https://www.hindustantimes.com/ht-img/img/2024/02/05/1600x900/quote-1_1707106565196_1707106574985.jpg',
      quote: '“The best way to get started is to quit talking and begin doing.” – Walt Disney',
    },
    {
      image: 'https://www.hindustantimes.com/ht-img/img/2024/02/05/1600x900/quote-2_1707106565842_1707106575175.jpg',
      quote: '“Don’t let yesterday take up too much of today.” – Will Rogers',
    },
  ],
  space: [
    {
      image: 'https://www.hindustantimes.com/ht-img/img/2024/02/05/1600x900/quote-3_1707106566118_1707106575349.jpg',
      quote: '“Space is the breath of art.” – Frank Lloyd Wright',
    },
    {
      image: 'https://www.hindustantimes.com/ht-img/img/2024/02/05/1600x900/quote-4_1707106566355_1707106575523.jpg',
      quote: '“The cosmos is within us. We are made of star-stuff.” – Carl Sagan',
    },
  ],
};

function StoryViewer({ id }) {
  const [index, setIndex] = useState(0);
  const slides = allStories[id] || [];

  const next = () => setIndex((index + 1) % slides.length);
  const prev = () => setIndex((index - 1 + slides.length) % slides.length);

  if (!slides.length) return <p>No story found for "{id}"</p>;

  return (
    <div className="story-viewer">
      <div
        className="story-slide"
        style={{ backgroundImage: `url(${slides[index].image})` }}
      >
        <div className="story-quote">
          <p>{slides[index].quote}</p>
        </div>

        <div className="story-nav">
          <button onClick={prev}>&#10094;</button>
          <button onClick={next}>&#10095;</button>
        </div>
      </div>
    </div>
  );
}

export default StoryViewer;
