import React, { useState } from 'react';
import Header from '../../components/Header/Header';
import StoryGrid from '../../components/StoryGrid/StoryGrid';
import Stories from '../../components/Stories/Stories'; // ✅ using this instead

function Home() {
  const [selectedStoryId, setSelectedStoryId] = useState(null);

  return (
    <div>
      <Header />

      <div>
        <h2 style={{ textAlign: 'center' }}>Latest Stories</h2>

        <StoryGrid onSelectStory={(id) => setSelectedStoryId(id)} />

        {selectedStoryId && (
          <>
            <h3 style={{ textAlign: 'center', marginTop: '2rem' }}>
              Now Showing: {selectedStoryId.toUpperCase()} Story
            </h3>
            <Stories id={selectedStoryId} /> {/* ✅ replaced StoryViewer */}
          </>
        )}
      </div>
    </div>
  );
}

export default Home;
