import React, { useState } from 'react';
import './Stories.css'; // Add your styling here

function Stories({ data }) {
  const [index, setIndex] = useState(0);

  // Safety check
  if (!data || data.length === 0) return <p>No stories available</p>;

  const next = () => {
    setIndex((prevIndex) => (prevIndex + 1) % data.length);
  };

  const prev = () => {
    setIndex((prevIndex) => (prevIndex - 1 + data.length) % data.length);
  };

  const story = data[index]; // current story

  return (
    <div className="story-viewer">
      <div className="story-box">
        <img src={story.image || story.thumbnail} alt={story.title} className="story-full-img" />
        <div className="story-details">
          <h2>{story.title}</h2>
          <p>{story.quote}</p>
        </div>
        <div className="story-buttons">
          <button onClick={prev}>◀️ Prev</button>
          <button onClick={next}>Next ▶️</button>
        </div>
      </div>
    </div>
  );
}

export default Stories;
