// src/pages/TechInfo/TechInfo.js
import React, { useState } from 'react';
import Header from '../../components/Header/Header';
import StoryGrid from '../../components/StoryGrid/StoryGrid';
import Stories from '../../components/Stories/Stories';

function TechInfo() {
  const [selectedStoryId, setSelectedStoryId] = useState(null);

  return (
    <div className="container">
      <Header />
      <h2 style={{ textAlign: 'center' }} className="m-4">Latest Tech Stories</h2>

      <StoryGrid
        onSelectStory={(id) => setSelectedStoryId(id)}
        filterCategory="techinfo"
      />

      {selectedStoryId && (
        <>
          <h3 style={{ textAlign: 'center', marginTop: '2rem' }}>
            Now Showing: {selectedStoryId.toUpperCase()} Story
          </h3>
          <Stories id={selectedStoryId} />
        </>
      )}
    </div>
  );
}

export default TechInfo;
