import React from 'react';
import './StoryGrid.css';

const stories = [
  {
    id: 'quotes',
    title: 'Inspiring quotes to start your day',
    date: 'Jul 10, 2025',
    thumbnail: 'https://www.hindustantimes.com/ht-img/img/2024/02/05/1600x900/quote-1_1707106565196_1707106574985.jpg',
  },
  {
    id: 'space',
    title: 'Mind-blowing space phenomena',
    date: 'Jul 09, 2025',
    thumbnail: 'https://www.hindustantimes.com/ht-img/img/2024/02/05/1600x900/quote-3_1707106566118_1707106575349.jpg',
  },
];

function StoryGrid({ onSelectStory }) {
  return (
    <div className="story-grid">
      {stories.map((story) => (
        <div
          key={story.id}
          className="story-card"
          onClick={() => onSelectStory(story.id)}
        >
          <img src={story.thumbnail} alt={story.title} className="story-img" />
          <div className="story-info">
            <h4>{story.title}</h4>
            <p>{story.date}</p>
          </div>
        </div>
      ))}
    </div>
  );
}

export default StoryGrid;
