import React, { useState } from 'react';
import './Stories.css';

const slides = [
  {
    image: 'https://www.hindustantimes.com/ht-img/img/2024/02/05/1600x900/quote-1_1707106565196_1707106574985.jpg',
    quote: '“The best way to get started is to quit talking and begin doing.” – Walt Disney',
  },
  {
    image: 'https://www.hindustantimes.com/ht-img/img/2024/02/05/1600x900/quote-2_1707106565842_1707106575175.jpg',
    quote: '“Don’t let yesterday take up too much of today.” – Will Rogers',
  },
  {
    image: 'https://www.hindustantimes.com/ht-img/img/2024/02/05/1600x900/quote-3_1707106566118_1707106575349.jpg',
    quote: '“It’s not whether you get knocked down, it’s whether you get up.” – Vince Lombardi',
  },
  {
    image: 'https://www.hindustantimes.com/ht-img/img/2024/02/05/1600x900/quote-4_1707106566355_1707106575523.jpg',
    quote: '“People who are crazy enough to think they can change the world, are the ones who do.” – Rob Siltanen',
  },
];

function Stories() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const nextSlide = () => {
    setCurrentIndex((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentIndex((prev) => (prev - 1 + slides.length) % slides.length);
  };

  return (
    <div className="story-wrapper">
      <div
        className="story-slide"
        style={{ backgroundImage: `url(${slides[currentIndex].image})` }}
      >
        <div className="quote-text">{slides[currentIndex].quote}</div>

        <div className="nav-buttons">
          <button className="btn-nav" onClick={prevSlide}>⟨</button>
          <button className="btn-nav" onClick={nextSlide}>⟩</button>
        </div>
      </div>
    </div>
  );
}

export default Stories;
