import React, { useState } from 'react';
import StoryGrid from '../../components/StoryGrid/StoryGrid';
import Stories from '../../components/Stories/Stories';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';

function Home() {
  const [selectedStoryId, setSelectedStoryId] = useState(null);

  return (
    <div>
      <Header />
      <div className='container'>
        <h2 style={{ textAlign: 'center' }} className='m-4'>Latest Stories</h2>

        <StoryGrid onSelectStory={(id) => setSelectedStoryId(id)} />

       {selectedStoryId && (
  <>
    <h3 style={{ textAlign: 'center', marginTop: '2rem' }}>
      Now Showing: {selectedStoryId.toUpperCase()} Story
    </h3>
    <Stories id={selectedStoryId} />
  </>
)}

      </div>
      <div>
        <Footer />
      </div>
    </div>
  );
}

export default Home;
