  import React, { useEffect, useState } from 'react';
  import { useNavigate } from 'react-router-dom';
  import './StoryGrid.css';


  function StoryGrid() {
    const navigate = useNavigate();
    const [stories, setStories] = useState([]);

    useEffect(() => {
      fetch('http://localhost:5000/api/stories')  // 👈 your backend URL
        .then((res) => res.json())
        .then((data) => {
          setStories(data);
        })
        .catch((error) => {
          console.error('Error fetching stories:', error);
        });
    }, []);

    return (
      <div className="story-grid">
        {stories.map((story) => (
          <div
            key={story.id}
            className="story-card"
            onClick={() => navigate(`/story/${story.id}`)} // 📍 navigate to new page
          >
            <img src={story.image} alt={story.title} className="story-full-img" />
            {/* <div className="story-info">
              <h4>{story.title}</h4>
              <p>{story.date}</p>
            </div> */}
          </div>
        ))}
      </div>
    );
  }

  export default StoryGrid;
