import React, { useState } from 'react';
import StoryGrid from '../../components/StoryGrid/StoryGrid';
import Stories from '../../components/Stories/Stories';
import Header from '../../components/Header/Header';

function Electronic() {
  const [selectedStoryId, setSelectedStoryId] = useState(null);

  return (
    <div className="container">
      <Header />
      <div>
        <h2 style={{ textAlign: 'center' }} className="m-4">Latest Electronic Stories</h2>

        {/* Pass filterCategory prop to limit to only "electronic" */}
        <StoryGrid 
          onSelectStory={(id) => setSelectedStoryId(id)} 
          filterCategory="electronic"
        />

        {selectedStoryId && (
          <>
            <h3 style={{ textAlign: 'center', marginTop: '2rem' }}>
              Now Showing: {selectedStoryId.toUpperCase()} Story
            </h3>
            <Stories id={selectedStoryId} />
          </>
        )}
      </div>
    </div>
  );
}

export default Electronic;
