import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './Stories.css';

function Stories() {
  const { id } = useParams();
  const [story, setStory] = useState(null);
  const [loading, setLoading] = useState(true);
  const [index, setIndex] = useState(0); // to navigate images

  useEffect(() => {
    fetch(`http://localhost:5000/api/stories/${id}`)
      .then((res) => {
        if (!res.ok) throw new Error('Story not found');
        return res.json();
      })
      .then((data) => {
        setStory(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching story:', err.message);
        setLoading(false);
      });
  }, [id]);

  const next = () => {
    setIndex((prevIndex) =>
      story?.images ? (prevIndex + 1) % story.images.length : 0
    );
  };

  const prev = () => {
    setIndex((prevIndex) =>
      story?.images
        ? (prevIndex - 1 + story.images.length) % story.images.length
        : 0
    );
  };

  if (loading) return <p className="loading-text">Loading...</p>;
  if (!story || !story.images || story.images.length === 0)
    return <p className="error-text">No story images found</p>;

  return (
    <div className="story-viewer">
      <div className="story-box">
        <img
          src={story.images[index]}
          alt={story.title}
          className="story-full-img"
        />

        <div className="story-details">
          <h2>{story.title}</h2>
          <p>{story.quote}</p>
        </div>

        <div className="story-buttons">
          <button onClick={prev}>◀️ Prev</button>
          <button onClick={next}>Next ▶️</button>
        </div>
      </div>
    </div>
  );
}

export default Stories;
