// src/pages/Fashions.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Fashions.css'; // create this CSS for styling
import Header from '../../components/Header/Header';

function Fashions() {
  const [stories, setStories] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:5000/api/stories')
      .then((res) => res.json())
      .then((data) => {
        // Filter where category === "fashions" (case-insensitive)
        const fashionStories = data.filter(
          (story) => story.category?.toLowerCase() === 'fashions'
        );
        setStories(fashionStories);
      })
      .catch((err) => console.error('Error fetching stories:', err));
  }, []);

  return (
    <div><Header/>
          <h2 style={{ textAlign: 'center' }} className='m-4'>Latest Stories</h2>
    <div className="fashions-container">
      <h2>Fashions</h2>
      <div className="story-grid">
        {stories.map((story) => (
          <div
            key={story._id}
            className="story-card"
            onClick={() => navigate(`/story/${story._id}`)}
          >
            <img src={story.thumbnail} alt={story.title} className="story-img" />
            <h4>{story.title}</h4>
          </div>
        ))}
      </div>
    </div>
    </div>
  );
}

export default Fashions;
