import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './Stories.css';

function Stories() {
  const { id } = useParams();
  const [story, setStory] = useState(null);
  const [loading, setLoading] = useState(true);
  const [index, setIndex] = useState(0);

  useEffect(() => {
    fetch(`http://localhost:5000/api/stories/${id}`)
      .then((res) => {
        if (!res.ok) throw new Error('Story not found');
        return res.json();
      })
      .then((data) => {
        setStory(data);
        setIndex(0);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching story:', err.message);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <p className="loading-text">Loading...</p>;
  if (!story) return <p className="error-text">Story not found</p>;

  const next = () => setIndex((prev) => (prev + 1) % story.images.length);
  const prev = () => setIndex((prev) => (prev - 1 + story.images.length) % story.images.length);

  return (
    <div
      className="story-viewer"
      style={{ backgroundImage: `url(${story.images[index]})` }}
    >
      <div className="story-glass">
        <button className="arrow left" onClick={prev}>❮</button>
        <div className="story-center">
          <img
            src={story.images[index]}
            alt={`Slide ${index}`}
            className="story-img"
          />
          <div className="story-caption">
            <h2>{story.title}</h2>
            <p>{story.quote}</p>
          </div>
        </div>
        <button className="arrow right" onClick={next}>❯</button>
      </div>
    </div>
  );
}

export default Stories;
